import React, { useCallback, useMemo, ReactNode } from "react";
import { FileWithPath, useDropzone } from "react-dropzone";
import "./index.css";

const baseStyle = {
  width: 200,
  height: 150,
  margin: 10
};
const borderNormalStyle = {
  border: "1px dotted #888"
};
const borderDragStyle = {
  border: "1px solid #00f",
  transition: "border .5s ease-in-out"
};
function App() {

  const onDrop = useCallback((acceptedFiles) => {
    // Do something with the files
    console.log("acceptedFiles:", acceptedFiles);
  }, []);
  const { getRootProps, getInputProps, isDragActive, open, acceptedFiles } = useDropzone({ onDrop });
  const fileList = (files: FileWithPath[]): ReactNode => (
    files.map(file => (
      <li key={file.path}>
        {file.path} - {file.size} bytes
      </li>
    ))
  );
  return (
    <div {...getRootProps({ className: 'dropzone' })}>

      <input {...getInputProps()} />
      {isDragActive ? (
        <p>ここにファイルをドラック＆ドロップする</p>
      ) : (
        <aside>
          <h4>Files</h4>
          <ul>{fileList(acceptedFiles)}</ul>
        </aside>
      )}
    </div>
  );
}

export default App;
